import {GoMail} from "react-icons/go";
import css from "styled-jsx/css";  
import fetch from "isomorphic-unfetch"; 

const style = css`
    .profile-box {
        width: 25%;
        max-width: 272px;
        margin-right: 26px;
    }

    .profile-image-wrapper {
        width: 100%;
        border: 1px solid #e1e4e8;
    }

    .profile-image-wrapper .profile-image {
        display: block;
        width: 100%;
    }

    .profile-username {
        margin: 0;
        padding-top: 16px;
        font-size: 26px;
    }

    .profile-user-login {
        margin: 0;
        font-size: 20px;    
    }

    .profile-user-bio {
        margin: 0;
        padding-top: 16px;
        font-size: 14px;
    }

    .profile-user-info {
        display: flex;
        align-items: center;
        margin: 4px 0 0;
    }

    .profile-user-info-text {
        margin-left: 6px;
    }
`;

const Profile = ({ user }) => {
    if (!user) {
        return null;
    }
    return (
        <>
            <div className="profile-box">
                <div className="profile-image-wrapper">
                    <img className="profile-image" src={user.avatar_url} alt={`${user.name}`} />
                </div>
                <h2 className="profile-username">{user.name}</h2>
                <p className="profile-user-login">{user.login}</p>
                <p className="profile-user-bio">{user.bio}</p>
                <p className="profile-user-info">
                    <GoMail size={16} color="#6a737d" />
                    <span className="profile-user-info-text">{user.email}</span> </p>
            </div>
            <style jsx>{style}</style>
        </>
    );
};

export const getServerSideProps = async ({ params }) => {
    const { name } = params;
    try {
        const res = await fetch(`https://api.github.com/users/${name}`);
        if (res.status === 200) {
            const user = await res.json();
            return { props: { user } };
        }
        return { props: {} };
    } catch (e) {
        console.log(e);
        return { props: {} };
    }
};

export default Profile;
