import fetch from "isomorphic-unfetch";
import { GoMail } from "react-icons/go";
import css from "styled-jsx/css"; 

const style = css`
  .user-contents-wrapper {
    display: flex;
    justify-content: center;
    padding-top: 50px;
  }
  .repos-wrapper {
    width: 100%;
    height: 100vh;
    overflow: scroll;
    padding: 0px 16px;
  }

  .repos-header {
    padding: 16px 0;
    font-size: 14px;
    font-weight: 600;
    border-bottom: 1px solid #e1e4e8;
  }

  .repos-count { 
    display: inline-block;
    padding: 2px 5px;
    margin-left: 6px;
    font-size: 12px;
    font-weight: 600;
    line-height: 1;
    color: #586069;
    background-color: rgba(27, 31, 35, 0.08);
    border-radius: 20px;
  }
`;

const name = ({ user, repos }) => {
  if (!user) {
    return <div>No user found</div>;
  }

  return (
    <div className="user-contents-wrapper">
      <div className="profile-box">
        <img className="profile-image" src={user.avatar_url} alt={`${user.name}'s avatar`} />
        <h1 className="profile-username">{user.name}</h1>
        <p className="profile-user-info">{user.bio}</p>
        {user.email && (
          <p className="profile-user-info">
            <GoMail size={16} color="#6a737d" />
            <span>{user.email}</span>
          </p>
        )}
        <p className="profile-user-info">Current Time: {time}</p>
        <div className="repos-wrapper">
          <div className="repos-header">
            Repositories
            <span className="repos-count">{repos.length}</span>
          </div>
          <ul className="repos">
            {repos.map((repo) => (
              <li key={repo.id} className="repo-item">
                <a href={repo.html_url} target="_blank" rel="noopener noreferrer" className="repo-link">
                  {repo.name}
                </a>
              </li>
            ))}
          </ul>
        </div>
        <style jsx>{style}</style>
      </div>
    </div>
  );
};

export const getServerSideProps = async ({ query }) => {
  const { name } = query;
  try {
    const userRes = await fetch(`https://api.github.com/users/${name}`);
    const reposRes = await fetch(`https://api.github.com/users/${name}/repos?sort=updated&page=1&per_page=10`);
    const user = await userRes.json();
    const repos = await reposRes.json();
    return { props: { user, repos, time: new Date().toISOString() } };
  } catch (e) {
    console.log(e);
    return { props: {} };
  }
};

export default name;
